import React from 'react'
import Typical from 'react-typical'
import "../style/HeaderText.scss";


function HeaderText() {    

    const traits = 
    [
    'Software engineer.', 1000,
    'Frontend developer.', 1000,
    'Fullstack developer.', 1000,
    'Game developer.', 1000,
    ]

    return (
        <section className="landingPage">
            <div className="landingPage__textWrapper">
                <h3>Hello, I'm <span>Alberto</span>, a 
                <span><Typical
                        loop={Infinity}
                        wrapper="b"
                        steps={traits}
                    /></span>
                </h3>  
                <p>I'm a Software Engineer originally from Portugal, currently living in Berlin, Germany. Well-organized and hardworking person with a creativity vein and attention to detail. Always keen to improve my skills and to learn new technologies that excite me. Animal lover, huge fan of climbing, hiking and photography. Interested in the entire software development spectrum with a special passion for frontend and design.</p>

            </div>
        </section>  
    )
}

export default HeaderText
