import React, { useEffect, useRef } from "react";
import { Html } from "drei";
import Model from "./model"
import { useFrame } from "react-three-fiber";
import { useInView } from "react-intersection-observer";


function HTMLContent({ yPosition, gltfPath, bgColor, rightSide, ...props }) {

    const ref = useRef()
    useFrame(() => (rightSide ? ref.current.rotation.y -= 0.002 : ref.current.rotation.y += 0.002))
    const [refItem, inView] = useInView({
        threshold: 0
    })
    useEffect(() => {
        inView && (document.body.style.background = bgColor)
    }, [inView])

    const xPosition = rightSide ? 140 : -10

    return(
            <group position={[xPosition, yPosition, 0]}>

                <div>
                    <mesh ref={ref} position={[-70, -10, 0]} scale={[0.9,0.9,0.9]}> 
                        <Model gltfPath={gltfPath}></Model>
                    </mesh>

                </div>


                <Html portal={props.domContent} >
                    <div ref={refItem}>{props.children}</div>
                </Html>

            </group>
    )
}

export default HTMLContent
