import React, {useEffect} from "react";
import "./style/App.scss";

import Navigation from "./components/Navigation";
import { Intro } from "./sections/Intro";
import Timeline  from "./sections/Timeline";
import Footer from "./components/Footer";
import { BgParticles } from "./components/BgParticles";
import particles from "./constants/particlesConfig";

function App() {

  useEffect(()=> {
    const appContainer = document.getElementById("AppContainer");
    appContainer.classList.add("app__container--visible");

    const titles = document.querySelectorAll('.title');
    const observer = new IntersectionObserver((entries) => {
      entries.forEach( entry => {
        if(entry.isIntersecting === true) {
          entry.target.classList.add('animate__text');
        }else{
          entry.target.classList.remove('animate__text');
        }
      })
    })

    titles.forEach( title => {
      observer.observe(title);
    })
  })

  return (
      <main className="app">
        <div id="AppContainer" className="app__container">
          <Navigation/>
          <BgParticles 
            className="particles"
            particles={particles}
          />
          <Intro/>
          <Timeline/>
          <Footer/>
        </div>
      </main>
  );
}

export default App;
