import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import "../style/Email.scss";


export const Email = () => {

    const [emailSent, setEmailSent] = useState(false);

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_lpht0s5', 'template_m8yj6bk', form.current, 'Ng_lvRzLCb008pGkw')
        .then((result) => {
            console.log(`Email sent. HTTP Status: ${result.status}`);
            setEmailSent(true);
        }, (error) => {
            console.log(error.text);
        });
    };

    return (
        <form className="email" ref={form} onSubmit={sendEmail}>
            <input className="email__subject" type="text" name="subject" placeholder='Subject'/>
            <input className="email__id" type="email" name="email" placeholder='Your Email'/>
            <input className="email__sendBtn" type="submit" value="Send" disabled={emailSent ? true : false} />
            <textarea className="email__content" name="message" placeholder='Your kind message'/>
        </form>
    );
};
