import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import "../style/Email.scss";


export const Email = () => {
    const [subjectText, setSubjectText] = useState('');
    const [emailText, setEmailText] = useState('');
    const [contentText, setContentText] = useState('');
    const [emailSent, setEmailSent] = useState(false);
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();
        if(validateFields()) {
            emailjs.sendForm('service_lpht0s5', 'template_m8yj6bk', form.current, 'Ng_lvRzLCb008pGkw')
            .then((result) => {
                console.log(`Email sent. HTTP Status: ${result.status}`);
                setEmailSent(true);
                clearFields();
            }, (error) => {
                console.log(error.text);
            });
        }
    };

    const validateFields = () => {
        if(subjectText === '') { console.log('Please add a Subject'); return false } 
        if(emailText === '') { console.log('Please add your Email'); return false } 
        if(contentText === '') { console.log('Please add write something kind'); return false } 
        if(contentText !== '' && emailSent !== '' && contentText !== '') return true;

    }

    const clearFields = () => {
        setSubjectText('');
        setEmailText('');
        setContentText('');
    }

    return (
        <form className="email" ref={form} onSubmit={sendEmail}>
            <input 
                className="email__subject"
                type="text" 
                name="subject" 
                placeholder='Subject' 
                value={subjectText}
                onChange={ e => setSubjectText(e.target.value)}/>
            <input 
                className="email__id" 
                type="email" name="email" 
                placeholder='Your Email' 
                value={emailText}
                onChange={ e => setEmailText(e.target.value)}/>
            <input className="email__sendBtn" 
                type="submit" 
                value="Send" 
                disabled={emailSent ? true : false}/>
            <textarea 
                className="email__content" 
                name="message" 
                placeholder='Your kind message' 
                value={contentText}
                onChange={ e => setContentText(e.target.value)}/>
        </form>
    );
};
