import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';
import "../style/Email.scss";

export const Email = () => {
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_lpht0s5', 'template_m8yj6bk', form.current, 'Ng_lvRzLCb008pGkw')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    };

    return (
        <form className="email" ref={form} onSubmit={sendEmail}>
            <input className="email__subject" type="text" name="subject" placeholder='Subject'/>
            <input className="email__id" type="email" name="email" placeholder='Your Email'/>
            <textarea className="email__content" name="message" placeholder='Your Kind Message'/>
            <input type="submit" value="Send" />
        </form>
    );
};
