import React, {useState, useEffect} from 'react'
import { useSpring, animated} from 'react-spring'

import { ReactComponent as SpringIcon} from '../assets/Spring.svg'
import { ReactComponent as JavaIcon} from '../assets/Java.svg'
import { ReactComponent as KotlinIcon} from '../assets/Kotlin.svg'

function Skills2(){
    const [active, setActive] = useState(false);

    const  springProps  = useSpring({
        config: { duration: 800 },
        from: { opacity: 1},
        to: { opacity: 0 }
    });
    const AnimatedBox = styled(animated(Icon/IconTag))`...`;


    useEffect(() => {
      const id = setTimeout(() => {
        setActive(!active);
      }, 2000);

      return () => clearTimeout(id);
    }, [active]);

    useEffect(() => {
      setActive(true);
    }, []);

    return (
      <div>
        <Icon style={{springProps}}/>
      </div>
    );
  }

export default Skills2
