import { createRef } from "react";
import { useMediaQuery } from 'react-responsive';

const isMobile = useMediaQuery({ query: `(max-width: 900px)` });
const value =  3

const state = {
  sections: {value},
  pages: {value},
  zoom: 1,
  top: createRef(),
};

export default state;
