import React from 'react'
import "../style/Skills.scss";

function Skills2() {

    const THICKNESS = 5;
const FONT_SIZE = 11;

const Glow = ({color, id}) => (
  <filter id={id} filterUnits="userSpaceOnUse" x="-10" y="-10" width="120" height="120">
    <feDropShadow dx="0" dy="1" stdDeviation="2" floodColor={color} floodOpacity="0.2"/>
    <feDropShadow dx="0" dy="-1" stdDeviation="2" floodColor={color} floodOpacity="0.2"/>
  </filter>
);

const Gradient = ({from, to, id}) => (
    <linearGradient id={id} x1='0%' y1='0%' x2='100%' y2='0%'>
        <stop offset='0%' stopColor={from} />
        <stop offset='100%' stopColor={to} />
    </linearGradient>
);

const Layer = ({text, gradient, size, offset = [0, 0, 0]}) => {
    const w = size, h = 40 * (size / 100), t = THICKNESS;
    const gid = Math.trunc(Math.random() * 10000 + 10000);
    const fid = Math.trunc(Math.random() * 10000 + 10000);
    return (
        <g className='layer' style={{
            '--offset-x': `${offset[0]}px`,
            '--offset-y': `${offset[1]}px`,
            '--offset-z': `${offset[2]}px`,
            '--size': `${size}`,
        }}>
            <path d={`M0,${h/2+t} v${-t} L${w/2},${0} L${w},${h/2} v${t} L${w/2},${h+t} Z`} fill={`url(#${gid})`} filter={`url(#${fid})`}/>
            <path d={`M0,${h/2+t} v${-t} L${w/2},${h} v${t} Z`}/>
            <path d={`M${w/2},${h+t} v${-t} L${w},${h/2}  v${t} Z`}/>
            <text x={0} y={0} dominantBaseline='middle' textAnchor='middle' style={{fontSize: FONT_SIZE * size / 100}}>{text}</text>
            <defs>
              <Gradient id={gid} from={gradient[0]} to={gradient[1]}/>
              <Glow id={fid} color={`rgba(0,0,0,1)`}/>
            </defs>
        </g>
    );
}

const Layers = ({children}) => (
    <svg className='layers' viewBox='0 0 100 90' xmlns='http://www.w3.org/2000/svg'>
        {children}
    </svg>
);

    return(
        <Layers>
            <Layer text='Kotlin' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[52, 75, 0]}  size={48}/>
            <Layer text='Java' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[0, 75, 0]} size={48}/>
            <Layer text='Spring' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[26, 85, 0]} size={48}/>
            <Layer text='Docker' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[78, 85, 0]} size={48}/>

            <Layer text='HTML' gradient={['rgba(53,35,38,0.75)', 'rgba(100,66,72,0.75)']} offset={[52, 45, 0]}  size={48}/>
            <Layer text='JS' gradient={['rgba(53,35,38,0.75)', 'rgba(100,66,72,0.75)']} offset={[0, 45, 0]} size={48}/>
            <Layer text='CSS' gradient={['rgba(53,35,38,0.75)', 'rgba(100,66,72,0.75)']} offset={[26, 55, 0]} size={48}/>
            <Layer text='React' gradient={['rgba(53,35,38,0.75)', 'rgba(100,66,72,0.75)']} offset={[-26, 55, 0]} size={48}/>

            <Layer text='Blender' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[52, 15, 0]}  size={48}/>
            <Layer text='Unity' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[0, 15, 0]} size={48}/>
            <Layer text='C#' gradient={['rgba(17,26,40,0.75)', 'rgba(29,44,67,0.75)']} offset={[26, 25, 0]} size={48}/>
        </Layers>
    )
}

export default Skills2
