import React, {useEffect, useRef} from 'react';
import Particles from 'react-tsparticles';
import { loadFull, load } from "tsparticles";

export const BgParticles = (props) => {    
    const particlesInit = async () => { 
      await load("tsparticles-custom", {
        fullScreen: {
          enable: false
        },
        zIndex: -1
        /* all other options */
      });
    };

    const particles = useRef();

    useEffect(()=> {
      const particles = document.getElementById('tsparticles');
      particles.firstChild.style.cssText =  "z-index: -1 !important";
      console.log(particles.firstChild.style)
    },[]);


    return (
      <Particles 
          init={particlesInit}
          options={props.particles}
          ref={particles}
      />
    );
}
